## 36(rows) x 90(columns) of space we can play
## 38(rows) x 92(columns) of total grid with boundaries included

def output(grid_copy):          #print the grid fastly
    for i in range(len(grid_copy)):
        grid_copy[i]=''.join(grid_copy[i])
    print('\n'.join(grid_copy))


for i in range(10):         #Place ten fruits at rondom positions
    grid[random.randint(1,36)][random.randint(1,90)]='@'


position=[19,46]
grid_copy=copy.deepcopy(grid)
prev_key,key=[0,0],''
prev_position=[copy.deepcopy([position[0],position[1]+1]),copy.deepcopy(position)]
score=0
timeT=0.15

while True:
    os.system('cls')

    if position[0] in [0,37] or position[1] in [0,91]:          #check if snake hit the boundaries
        print("Your are Out!!  (You reached out of boundary)")
        break
    
    if grid_copy[position[0]][position[1]]=='@':                            #check if snake eaten the fruit + add another one if eaten
        grid[position[0]][position[1]]=' '
        grid[random.randint(1,36)][random.randint(1,90)]='@'
        score+=1

    if prev_position[-1]!=position:                                         #add new positions of the head of the snake
        prev_position.append(copy.deepcopy(position))
        if len(prev_position)>score+2:                                      #removes the extra positions
            prev_position.pop(0)
        try:
            for i in range(1,len(prev_position)-1):                             # add the '#' between snake
                grid_copy[prev_position[i][0]][prev_position[i][1]]='#'
        except:
            pass

    grid_copy[prev_position[0][0]][prev_position[0][1]],grid_copy[prev_position[-1][0]][prev_position[-1][1]]='+','%'               # add head and tail of snake

    if position in prev_position[:-1]:
        print("You are out!!! (You have eaten yourself)")
        break


    try:
        if msvcrt.kbhit():                                          # Check if a key was pressed
            key = msvcrt.getch().decode('utf-8').lower()            #receive the key and decode it binary form to normal form
            if prev_key[-1]!=key:
                prev_key[0],prev_key[1]=prev_key[1],key


        if (prev_key[0]=='w' and prev_key[1]=='s'):                    #snake movement defined here
            position[0]-=1
            timeT=0.22
        elif (prev_key[0]=='s' and prev_key[1]=='w'):
            position[0]+=1
            timeT=0.22
        elif (prev_key[0]=='a' and prev_key[1]=='d'):
            position[1]-=1
            timeT=0.12
        elif (prev_key[0]=='d' and prev_key[1]=='a'):
            position[1]+=1    
            timeT=0.12
                               
        elif key == 'w':
            position[0]-=1
            timeT=0.22
        elif key == 'a':
            position[1]-=1
            timeT=0.12
        elif key == 's':
            position[0]+=1
            timeT=0.22
        elif key == 'd':
            position[1]+=1
            timeT=0.12
    except:
        pass

    output(grid_copy)
    print(f"Score: {score} ; Keys: {prev_key} ; Postion {position} ; All Postions {prev_position}")
    grid_copy=copy.deepcopy(grid)
    time.sleep(timeT)

print(f"Score: {score}")
